#include "common/headers.h"
/**
 * @brief The Solution class
 * 给定两个字符串 s 和 p，找到 s 中所有 p 的 异位词 的子串，返回这些子串的起始索引。不考虑答案输出的顺序。

异位词 指字母相同，但排列不同的字符串。



示例 1:

输入: s = "cbaebabacd", p = "abc"
输出: [0,6]
解释:
起始索引等于 0 的子串是 "cba", 它是 "abc" 的异位词。
起始索引等于 6 的子串是 "bac", 它是 "abc" 的异位词。
 示例 2:

输入: s = "abab", p = "ab"
输出: [0,1,2]
解释:
起始索引等于 0 的子串是 "ab", 它是 "ab" 的异位词。
起始索引等于 1 的子串是 "ba", 它是 "ab" 的异位词。
起始索引等于 2 的子串是 "ab", 它是 "ab" 的异位词。
 */
class Solution {
 public:
  vector<int> findAnagrams(string s, string p) {
    if (s.size() < p.size())
      return {};
    vector<int> res;
    unordered_map<char, long> map_;
    unordered_map<char, bool> map2_;
    for (auto i : p) {
      map_[i]++;
      map2_[i] = true;
    }
    long match_num = 0;
    for (long i = 0; i < p.size(); ++i) {
      if (map_[s[i]] > 0) {
        match_num++;

      }
      if (map2_[s[i]])
        map_[s[i]]--;
    }
    if (match_num == p.size()) {
      res.push_back(0);
    }
//    cout << map_[s[0]] << endl;
    if (map2_[s[0]]) {
      map_[s[0]]++;
      if (map_[s[0]] > 0)
        match_num--;
    }
//    cout << match_num << endl;
    long winsize = p.size() - 1;
    for (long i = 1; i < s.size(); ++i) {
      long end_id = i + winsize;

      if (map_[s[end_id]] > 0) {
        match_num++;
      }
      if (map2_[s[end_id]])
        map_[s[end_id]]--;

//      cout << i << " " << map_['a'] << " " << map_['b'] << " " << map_['c'] << " " << match_num << endl;

      if (match_num == p.size()) {
        res.push_back(i);
      }
      if (map2_[s[i]]) {
        map_[s[i]]++;
        if (map_[s[i]] > 0)
          match_num--;
      }
    }
    return res;
  }
};

int main() {
  string s = "",
         p = "yqrbgjdwtcaxzsnifvhmou";
  Solution solution;
  auto res = solution.findAnagrams(s, p);
  printVector(res);
}
